import React, { useEffect, useState } from 'react';
import axios from 'axios';
import BBCard from './BBCard';
import '../styles/Series.css';

export default function Series() {
    const [BBList, setBBList] = useState([])
    useEffect(() => {
        axios
            .get('https://tarea-1-breaking-bad.herokuapp.com/api/episodes?series=Breaking+Bad')
            .then(response => {
                console.log(response.data);
                setBBList(response.data);
            })
            .catch(err => {
                console.log(err)
            })
    }, [])

    var seasons = [];
    for (var key in BBList) {
        if (seasons.includes(parseInt(BBList[key].season))) {
            // pass
        } else {
            seasons.push(parseInt(BBList[key].season))
        } 
    }

    var chaptersPerSeason = {};
    for (var season in seasons) {
        chaptersPerSeason[season] = {}
    }

    for (var key2 in BBList) {
        for (var season2 in chaptersPerSeason) {
            if (BBList[key2].season === season2) {
                chaptersPerSeason[season2].push(BBList[key2])
            } 
        }
    }

    console.log(chaptersPerSeason);

    return(
        <section className='seasons-container'>
            {seasons.map((value, id) => {
                return <BBCard value={value} id={id} />
            })}
        </section>
    )
}
